mapper.select:
select
  id, dict_id, type_id, dict_name, dict_msg, dict_status
from score.dict


        and dict_id = #{dictId}


        and type_id = #{typeId}


limit 1

select
id, dict_id, type_id, dict_name, dict_msg, dict_status
from score.dict


        and id = #{id}


        and dict_id = #{dictId}


        and type_id = #{typeId}


        and dict_name = #{dictName}


        and dict_msg = #{dictMsg}


        and dict_status = #{dictStatus}


        group by ${groupByStr}
